package org.reportingapi.service.impl;

import com.fasterxml.jackson.core.JsonProcessingException;
import com.fasterxml.jackson.databind.ObjectMapper;
import lombok.extern.slf4j.Slf4j;
import org.reportingapi.dto.request.RequestApiRequest;
import org.reportingapi.service.ReportingApiService;
import org.springframework.beans.factory.annotation.Autowired;
import org.springframework.http.*;
import org.springframework.stereotype.Service;
import org.springframework.web.client.HttpStatusCodeException;
import org.springframework.web.client.RestTemplate;

import java.util.*;

@Service
@Slf4j
public class ReportingApiServiceImpl implements ReportingApiService {
    private static final String REPORTING_API_URL = "https://gw-fatoora.zatca.gov.sa/e-invoicing/developer-portal/invoices/reporting/single";
    private final RestTemplate restTemplate;

    @Autowired
    public ReportingApiServiceImpl(RestTemplate restTemplate) {
        this.restTemplate = restTemplate;
    }

    @Override
    public String reportingApiInfo(String username, String password, String acceptLanguage, String clearanceType) throws JsonProcessingException {
        ResponseEntity<String> result = null;
        RequestApiRequest requestApiRequest = new RequestApiRequest();
        requestApiRequest.setInvoiceHash("zTUDjwJjoMOHqF3+J6IYHbJWXvLjLqMj9Gh3peYOn0s=");
        requestApiRequest.setUuid("6f4d20e0-6bfe-4a80-9389-7dabe6620f12");
        requestApiRequest.setInvoice("");
        String authorization = username + ":" + password;
        try {
            HttpHeaders headers = new HttpHeaders();
            headers.setAccept(Collections.singletonList(MediaType.APPLICATION_JSON));
            headers.set("accept-language", acceptLanguage);
            headers.set("Clearance-Status", clearanceType);
            headers.set("Accept-Version", "V2");
            headers.set("Authorization", "Basic " + Base64.getEncoder().encodeToString(authorization.getBytes()));
            headers.setContentType(MediaType.APPLICATION_JSON);
            HttpEntity<String> request = new HttpEntity<>(requestApiRequest.toString(), headers);
            result = restTemplate.exchange(REPORTING_API_URL, HttpMethod.POST, request, String.class);
        } catch (HttpStatusCodeException e) {
            System.out.println(e);
        }
        ObjectMapper mapper = new ObjectMapper();
        String body = Objects.requireNonNull(result).getBody();
        System.out.println(mapper.writer().withDefaultPrettyPrinter().writeValueAsString(mapper.readTree(body)));
        return body;
    }
}
